import React from 'react'
import './App.css';
import Tabs from './Tabs';
import Tab from './Tab';
import FetchRandom from './FetchRandom';

class App extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      selected : '1 Week'
    }
  }
  setSelected = (tab) =>{
    this.setState({selected: tab})
  }
  render() {
    return (
      <div className="App mt-4">
        <Tabs tabs= {['1 Minute','5 Minutes', '1 Hour', '1 Week']} selected = {this.state.selected} setSelected={this.setSelected}>
          <Tab isSelected={ this.state.selected === '1 Minute'}>
            <FetchRandom timeStamps = '1 Minutes'></FetchRandom>
          </Tab>
          <Tab isSelected={ this.state.selected === '5 Minutes'}>
            <FetchRandom timeStamps = '5 Minutes'></FetchRandom>
          </Tab>
          <Tab isSelected={ this.state.selected === '1 Hour'}>
            <FetchRandom timeStamps = '1 Hours'></FetchRandom>
          </Tab>
          <Tab isSelected={ this.state.selected === '1 Week'}>
            <FetchRandom timeStamps = '24 Hours'></FetchRandom>
          </Tab>
        </Tabs>
      </div>
    );
  }
}

export default App;
