import React from 'react'
import './App.css';
import Tabs from './Tabs';
import Tab from './Tab';
import FetchRandom from './FetchRandom';

class App extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      selected : 'Home'
    }
  }
  setSelected = (tab) =>{
    this.setState({selected: tab})
  }
  render() {
    return (
      <div className="App mt-4">
        <Tabs tabs= {['1 Minute','5 Minutes', '1 Hour', '1 Week']} selected = {this.state.selected} setSelected={this.setSelected}></Tabs>
        <Tab isSelected={ this.state.selected === '1 Minute'}>
          <p>Hi from 1 Minute</p>
        </Tab>
        <Tab isSelected={ this.state.selected === '5 Minutes'}>
        </Tab>
        <Tab isSelected={ this.state.selected === '1 Hour'}>
          <p>Hi from 1 Hour</p>
        </Tab>
        <Tab isSelected={ this.state.selected === '1 Week'}>
          <p>Hi from 1 week</p>
        </Tab>
      </div>
    );
  }
}

export default App;
